mapper.select:
select DISTINCT cono 
    from messg 
     where sno = #{sno}

select mswr 
     from messg 
     where sno = #{sno} and cono = #{cono} 
     ORDER BY msno desc 
     limit 1;

select count(*)
from qna
where cono = #{contentsNo} and sno = #{memberNo}

select msge, mswr 
    from messg
    where cono = #{cono} and sno = #{sno}
